insert into PRD_BANKFINANCE values (null, '00045892', '2012年长江·鑫利第223期人民币98天产品', '重庆银行', '人民币', '98天', '2012-10-09', '2012-09-27', '2012-10-08', '98天', '5.2', '票据型', '10 万元', '浮动收益型', '全国', '10000', '无', '否', '', '1.银行可提前终止。2.投资者无提前赎回权。', '该产品所募集资金投资于全国银行间债券市场可流通债券。该产品为非保本浮动收益型，投资者主要面临信用风险：若债券发行人发生信用违约，则产品的收益甚至本金将受到不利影响。该产品适合保守、稳健、平衡、成长、进取型投资者。', '产品预期年化收益率为5.20%。',0, null,'2012-09-27 11:38:15.168009');
insert into PRD_BANKFINANCE values (null, '00045904', '7天自动滚续理财HJB1202G7D32', '中国银行', '澳元', '7天', '2012-10-08', '2012-09-27', '2012-10-07', '7天', '1.3', '票据型', '9000 澳元', '固定收益型', '全国', '100', '无', '否', '', '1、银行有权提前终止本产品。2、投资者无权提前赎回。', '该产品募集资金由中国银行股份有限公司统一运作管理，投资于公开评级在投资级以上的债券、票据、拆借等国内外金融市场工具。其中债券、票据等公开评级在投资级以上的金融产品投资比例为0-60%；拆借等货币市场工具投资比例为10%-90%。上述投资比例可在[-10%，+10%]区间内浮动。产品到期保本，收益稳定，经中行风险评级评为极低风险产品，流动性评级为高，适合评定为有投资经验及无投资经验的保守型、稳健型、平衡型、成长型、进取型客户。', '产品到期或由中国银行股份有限公司提前终止，收益率为1.30%。',0, null,'2012-09-27 11:38:16.046565');
insert into PRD_BANKFINANCE values (null, '00045650', '“聚财宝”灵活添利（保本）2012年174期人民币理财产品（平）', '平安银行（原）', '人民币', '61天', '2012-09-29', '2012-09-27', '2012-09-28', '61天', '4.0', '票据型', '5 万元', '浮动收益型', '全国', '1000', '超过预期最高年化收益率的收益部分作为平安银行的手续费。', '否', '', '1.银行根据市场情况可以到期终止或者提前终止本理财产品。2.理财期内，投资者无提前终止本理财计划的权利。', '投资于银行间市场信用等级较高、流动性较好的债券或货币市场工具，包括：债券回购、拆借、央行票据、国债、金融债、以及高信用级别的企业债券（企业债、公司债、短期融资券、中期票据等）等资产。该产品为保本浮动收益型，平安银行对该产品的风险评级为A（极低风险）。该产品适合经平安银行评估风险承受能力为“激进型”、“进取型”、“平衡型”、“稳健型”、“谨慎型”的投资者。', '根据当期可投资资产收益率，扣除产品的成本，本期理财产品持有到期年化收益率为4.00%。',0, null,'2012-09-27 11:38:17.179151');
insert into PRD_BANKFINANCE values (null, '00045906', '“丁香花惠融”229期05号人民币40天产品', '哈尔滨银行', '人民币', '40天', '2012-10-11', '2012-09-27', '2012-10-10', '40天', '4.0', '票据型', '5 万元', '浮动收益型', '全国', '10000', '无', '否', '', '1、因不可抗力，或市场发生剧烈波动，或由于国家法律法规及政策的重大调整或产品协议书约定的终止情形出现等原因，哈尔滨银行有权提前终止本理财产品。2、在理财期内，投资者无提前终止权。', '该产品投资于债券市场和货币市场金融工具，包括高信用等级的债券、短期金融债、同业存款和同业拆借等资产。该产品为保本浮动收益型，投资者主要面临信用风险：如果产品投资的债券及其他资产发行人发生信用违约，投资者到期收益将会受到不利影响。该产品适合稳健型投资者进行投资。', '该产品预期年化收益率为4.00%。',0, null,'2012-09-27 11:38:20.426667');
insert into PRD_BANKFINANCE values (null, '00045655', '“太阳添富2号19期”人民币12天滚动型产品', '广州农商银行', '人民币', '12天', '2012-09-28', '2012-09-27', '2012-09-27', '12天', '2.2', '票据型', '5 万元', '浮动收益型', '全国', '10000', '超过预期最高年化收益率部分作为服务费用。', '否', '', '1、银行可提前终止。2、客户无提前终止权。', '产品募集资金投资于符合国家政策的金融工具，包括但不限于现金、国债、金融债、央行票据、企业债、公司债、短期融资券、中期票据、债券回购、同业拆借、存放同业等投资工具。 产品到期保障本金，投资者主要面临信用风险：如果相关债券发行人或借款人未能按时足额支付本金和收益，投资者的收益将受到不利影响。该产品为保本浮动收益型，适合稳健型投资者。', '在投资于基础资产的本金和获得利息按时足额回收的情况下，本理财产品持有到期的预期收益率为2.20%。',0, null,'2012-09-27 11:38:21.475878');
